@Component({
  selector: 'app-to-do-list',
  templateUrl: './to-do-list.component.html',
  styleUrls: ['./to-do-list.component.scss']
})
export class ToDoListComponent {

}
<div id="page">
    <h1>Todo List</h1>
    <ul class="todo_table">
        <li>Buy a new gaming laptop <button class="del">Delete</button></li>
        <li>Complete previous task <button class="del">Delete</button></li>
        <li>Create some angular app <button class="del">Delete</button></li>
